INSERT INTO `tbl_static_page` (`id`, `slug`, `title`, `details`, `file_name`) VALUES
(1, 'message-of-head-teacher', 'প্রধান শিক্ষক এর বাণী', 'অধ্যক্ষ এর বানী মানুষ হিসাবে সর্বকালের যোগ্যতা অর্জনের প্রধান মাধ্যম হলো শিক্ষা । শিক্ষা জাতীয় উন্নয়নের মূল চাবিকাঠি বিধায় শিক্ষার উন্নয়ন ছাড়া জাতীয় উন্নয়ন সম্ভব নয় । শিক্ষা ব্যবস্থার মাধ্যমেই প্রতিফলিত হয় কোন জাতির আশা আকাঙ্খা, আর্থ-সাজাজিক ও সংস্কৃতিক জীবন প্রবাহ । আদর্শ ডিগ্রি মহাবিদ্যালয় খাবাশপুর জেলার দক্ষিন মানিকগঞ্জের একটি সর্বোচ্চ বিদ্যাপিঠ । উচ্চ শিক্ষার কোন সুযোগ না থাকায় অত্র এলাকার আপামর জন সাধারনের প্রানের দাবীর প্রেক্ষিতে এলাকার সমাজ সেবক, বিদ্যানুরাগী ব্যাক্তিদের সক্রিয় চেষ্ঠা ও সহযোগিতায় ১৯৮৪ সালের ৫ই আগষ্ট কলেজটি প্রতিষ্ঠিত হয় । নানা ঘাত প্রতিঘাত, সমস্যা-সংকট প্রতিকুলতা মোকাবেলা করে কলেজটি সম্প্রতি জেলার শ্রেষ্ট কলেজ হিসাবে গৌরব অর্জন করতে সক্ষম হয়েছে । শিক্ষা প্রতিষ্ঠান হল বিশ্ব ও দেশীয় জ্ঞান ও মূল্যবোধ সৃষ্টির কেন্দ্র বিন্ধু । শিক্ষা দান ও শিক্ষার আর্দশ প্রতিষ্ঠায় অগ্রণী ভূমিকা পালনের সুযোগ রয়েছে আদর্শ ডিগ্রি মহাবিদ্যালয় খাবাশপুর এর । কলেজের শিক্ষক মন্ডলী ও গভনিং বডির সদস্য বৃন্দ্র তাদের নিরলস শ্রম, দক্ষতা, মেধা, চেষ্ঠা, কর্মতৎপরতা ও আন্তরিকতার সাথে দায়িত্ব পালন করে কলেজটিকে মানিকগঞ্জ জেলার মধ্যে গৌররময় স্থানে রাখতে সক্ষম হয়েছে । পরিেেশষে আল্লাহ পাকের কাছে কলেজ পরিবারের সকল শিক্ষক, কর্মচারী, শিক্ষার্থী ও গর্ভনিং বডির সকল স¤œানিত সদস্যবৃন্দ এবং সর্বোপরি অত্র এলাকার সকল স্তরের শুভাকাংক্ষীদের সার্বিক কল্যাণ, মঙ্গল এবং কলেজের উত্তোত্তর বৃদ্ধি কামনা করছি । সদাশয় সরকারের ডিজিটাল বাংলাদেশ নির্মানে অত্র কলেজের সর্বাতœক প্রচেষ্টায় থাকার দৃঢ় অঙ্গিকার ব্যক্ত করছি । মোঃ হাফিজুর রহমান খান ভারপ্রাপ্ত অধ্যক্ষ আদর্শ ডিগ্রি মহাবিদ্যালয় খাবাশপুর, মানিকগঞ্জ। Phone- 01710851869 ‍ E-mail- admk4050@gmail.com Website- adarshacollegekm.edu.bd', '1530341580.jpg'),
(2, 'history', 'প্রতিষ্ঠানের ইতিহাস', 'খোন্দকার দেলোয়র হোসেন কলেজ, মানিকগঞ্জ শহরের প্রবেশদ্বার শহীদ স্মরণীর পাশে অবস্থিত। কলেজটি 2003 সালে 23 শে ফেব্রুয়ারী প্রতিষ্ঠিত হয়। মানসম্মত যোগউপযোগী শিক্ষার অভাবের তাগিদ থেকেই এ কলেজের জন্ম। কলেজটি মানিকগঞ্জের আপামর জনসাধারণের ইচ্ছারই একটি প্রতিফলন। কলেজটি প্রতিষ্ঠালগ্নে মূল নেতৃত্বে ছিলেন জাতীয় সংসদের তদানিন্তনকালীন চীপ হুইপ বিশিষ্ট রাজনীতিবিদ, ভাষাসৈনিক, বীরমুক্তিযোদ্ধা এবং বিএনপির প্রখ্যাত মহাসচিব মরহুম খোন্দকার দেলোয়ার হোসেন। কলেজটি প্রতিষ্ঠা লগ্নে তিনি প্রাথমিক ফান্ড ও জমি প্রদানের ব্যবস্থা করেছিলেন। তাই অত্র এলাকার জনগন তার নামেই কলেজটির নামকরণ করেন।তিনিই কলেজের মূল প্রতিষ্ঠাতা। প্রতিষ্ঠাতার মূল স্বপ্নই ছিল একটি মান সম্মত শিক্ষা প্রতিষ্ঠান গড়ে তোলা। আমরা সেই স্বপ্নকে ইস্পিত লক্ষে পৌছানোর জন্য নিরলস কাজ করে যাচ্ছি।\r\n\r\nকলেজটির জন্মলগ্নে যারা প্রত্যক্ষ সহযোগিতা করেছেন তার মধ্যে এ্যাডভোকেট মোকসেছুর রহমান, এ্যাডভোকেট আজাদ হোসেন খান, ডাঃ আকতারা খাতুন লুনা, খোন্দকার আব্দুল হামিদ খান ডাবলু ও খোন্দকার ডাঃ দেলোয়ারা বেগম পান্না উল্লেখযোগ্য। এছাড়া স্থানীয় অনেক আইনজীবি, ব্যবসায়ী, ঠিকাদার ও বিভিন্ন পেশার মানুষ কলেজ প্রতিষ্ঠালগ্নে সহযোগীতার হাত বাড়িয়ে দিয়েছেন। তারা চিরদিন স্মরনীয় হয়ে থাকবে। এতৎভিন্ন তদকালীন জেলা প্রশাসক ও পুলিশ সুপারের সহযোগীতায়ও উল্লেখযোগ্যভাবে স্মরন করা যায়।\r\n\r\nপ্রতিষ্ঠানটি উচ্চমাধ্যমিক পর্যায়ের শিক্ষা কাযক্রম সুনামের সহিত পরিচালনা করে আসছে। এখানে মানবিক, ব্যবসায় শিক্ষা ও বিজ্ঞান এই তিনটি বিভাগিই চালু আছে।কলেজের শিক্ষার মান ভাল। বর্তমানে ছাএ-ছাত্রীর সংখ্যা প্রায় 430 জন।\r\n\r\nঅত্র কলেজটি প্রতিষ্ঠার পর সুর্দীঘকাল অতিবাহিত হওয়ার পরেও এমপিও ভূক্ত না হওয়ায় আর্থিক সংকট হতে এখনো মুক্তি লাভ করতে পারেনি।', ''),
(3, 'infrastructure', 'প্রতিষ্ঠানের ভৌত অবকাঠামো', 'প্রতিষ্ঠানটির জমির পরিমাণ - 104 শতাংশ\r\n\r\nজমির ব্যবহার -\r\n\r\n                     কলেজ ভবন - 50 শতাংশ\r\n\r\n                     খেলার মাঠ -  54 শতাংশ', ''),
(4, 'goals-and-achievements', 'প্রতিষ্ঠানের লক্ষ্য ও অর্জন', 'বিদ্যালয়টি প্রয়োজনীয় উন্নয়ন ঘটিয়ে একটি উন্নত মান সম্পন্ন মাধ্যমিক শিক্ষা প্রতিষ্ঠানে উন্নত করব।', ''),
(5, 'admission-information', 'ভর্তির তথ্য', 'র্চ অভ্ গড উচ্চ বিদ্যালয় স্থাপিত ১৯৭০ সালে লালমনিরহাট জেলায় । ইহা ১৯৭১ সনে প্রতিষ্ঠিত এবং ০১/০১/১৯৭১ তারিখ থেকে মাধ্যমিক বিদ্যালয় হিসেবে স্বীকৃতি প্রাপ্ত। পশ্চাৎপদ ঢাকা হিসেবে খ্যাত হাজারীবাগ এলাকার এটি বালিকাদের একমাত্র জ্ঞানের আধার। আধুনিক একাডেমীক ভবন, প্রশস্ত খেলার মাঠ, কোলাহলমুক্ত নিরাপদ ও মনোরম প্রাকৃতিক পরিবেশে এ প্রতিষ্ঠানটি প্রতিষ্ঠালগ থেকে জ্ঞানের আলো চার্চ অভ্ গড উচ্চ বিদ্যালয় স্থাপিত ১৯৭০ সালে লালমনিরহাট জেলায় । ইহা ১৯৭১ সনে প্রতিষ্ঠিত এবং ০১/০১/১৯৭১ তারিখ থেকে মাধ্যমিক বিদ্যালয় হিসেবে স্বীকৃতি প্রাপ্ত। পশ্চাৎপদ ঢাকা হিসেবে খ্যাত হাজারীবাগ এলাকার এটি বালিকাদের একমাত্র জ্ঞানের আধার। আধুনিক একাডেমীক ভবন, প্রশস্ত খেলার মাঠ, কোলাহলমুক্ত নিরাপদ ও মনোরম প্রাকৃতিক পরিবেশে এ প্রতিষ্ঠানটি প্রতিষ্ঠালগ থেকে জ্ঞানের আলো বিতরণ করছে। কািরগির শিক্ষা ছাড়া জাতীয় উনতির কোন সহজ পথ নেই, এ উপলব্ধি থেকে ২০০৩ সালে এস এস সি ভোকেশনাল কোর্স চালুর মাধ্যমে প্রতিষ্ঠানটি নারী শিক্ষার ক্ষেত্রে সূচনা করেছে এক নতুন অধ্যায়ের, ফলে এটি পরিণত হয়েছে ঢাকা শহরের নারীদের একমাত্র কারিগরি বিদ্যাপীঠে। পশ্চাৎপদ ঢাকা হিসেবে খ্যাত হাজারীবাগ এলাকার এটি বালিকাদের একমাত্র জ্ঞানের আধার। আধুনিক একাডেমীক ভবন, প্রশস্ত খেলার মাঠ, কোলাহলমুক্ত নিরাপদ ও মনোরম প্রাকৃতিক পরিবেশে এ প্রতিষ্ঠানটি প্রতিষ্ঠালগ থেকে জ্ঞানের আলো বিতরণ করছে।বিতরণ করছে। কািরগির শিক্ষা ছাড়া জাতীয় উনতির কোন সহজ পথ নেই, এ উপলব্ধি থেকে ২০০৩ সালে এস এস সি ভোকেশনাল কোর্স চালুর মাধ্যমে প্রতিষ্ঠানটি নারী শিক্ষার ক্ষেত্রে সূচনা করেছে এক নতুন অধ্যায়ের, ফলে এটি পরিণত হয়েছে ঢাকা শহরের নারীদের একমাত্র কারিগরি বিদ্যাপীঠে। পশ্চাৎপদ ঢাকা হিসেবে খ্যাত হাজারীবাগ এলাকার এটি বালিকাদের একমাত্র জ্ঞানের আধার। আধুনিক একাডেমীক ভবন, প্রশস্ত খেলার মাঠ, কোলাহলমুক্ত নিরাপদ ও মনোরম প্রাকৃতিক পরিবেশে এ প্রতিষ্ঠানটি প্রতিষ্ঠালগ থেকে জ্ঞানের আলো বিতরণ করছে। চার্চ অভ্ গড উচ্চ বিদ্যালয় স্থাপিত ১৯৭০ সালে লালমনিরহাট জেলায় । ইহা ১৯৭১ সনে প্রতিষ্ঠিত এবং ০১/০১/১৯৭১ তারিখ থেকে মাধ্যমিক বিদ্যালয় হিসেবে স্বীকৃতি প্রাপ্ত। পশ্চাৎপদ ঢাকা হিসেবে খ্যাত হাজারীবাগ এলাকার এটি বালিকাদের একমাত্র জ্ঞানের আধার। আধুনিক একাডেমীক ভবন,