CREATE TABLE `tbl_student_attendance` (
  `id` int(10) NOT NULL COMMENT 'PK',
  `date` date NOT NULL,
  `class_id` tinyint(4) NOT NULL,
  `total_student` int(5) NOT NULL,
  `total_presence` int(5) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

